import React from 'react';
import './components.css';

const GRAPH_TYPES = ['line', 'bar', 'pie']; // Add more types as needed

const GraphTypeSidebar = ({ selectedGraphType, onGraphTypeChange }) => {
  return (
    <div className="sidebar">
      <h3>Graph Types</h3>
      <div className="sidebar-buttons">
        {GRAPH_TYPES.map((type) => (
          <button
            key={type}
            className={selectedGraphType === type ? 'active' : ''}
            onClick={() => onGraphTypeChange(type)}
          >
            {type.charAt(0).toUpperCase() + type.slice(1)}
          </button>
        ))}
      </div>
    </div>
  );
};

export default GraphTypeSidebar;
