import React from 'react';
import './components.css';

const DataTable = ({ data, onDataChange }) => {
  const handleInputChange = (e, rowIndex, key) => {
    const newData = [...data];
    newData[rowIndex][key] = isNaN(e.target.valueAsNumber) ? e.target.value : e.target.valueAsNumber;
    onDataChange(newData);
  };

  const addRow = () => {
    const newRow = { x: data.length + 1, y: 0 };
    onDataChange([...data, newRow]);
  };

  const removeRow = (rowIndex) => {
    const newData = data.filter((_, index) => index !== rowIndex);
    onDataChange(newData);
  };

  if (!data || data.length === 0) {
    return (
      <div>
        <p>No data to display.</p>
        <button onClick={addRow}>Add Row</button>
      </div>
    );
  }

  const headers = Object.keys(data[0]);

  return (
    <div className="data-table-container">
      <table className="data-table">
        <thead>
          <tr>
            {headers.map((header) => (
              <th key={header}>{header}</th>
            ))}
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {data.map((row, rowIndex) => (
            <tr key={rowIndex}>
              {headers.map((key) => (
                <td key={key}>
                  <input
                    type="number"
                    value={row[key]}
                    onChange={(e) => handleInputChange(e, rowIndex, key)}
                  />
                </td>
              ))}
              <td>
                <button className="remove-btn" onClick={() => removeRow(rowIndex)}>
                  Remove
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <button className="add-btn" onClick={addRow}>Add Row</button>
    </div>
  );
};

export default DataTable;
