import React from "react";
import "./components.css";

// SVG Icons for chart types
const LineIcon = () => (
  <svg
    xmlns="http://www.w3.org/2000/svg"
    width="24"
    height="24"
    viewBox="0 0 24 24"
    fill="none"
    stroke="currentColor"
    strokeWidth="2"
    strokeLinecap="round"
    strokeLinejoin="round"
  >
    <polyline points="2 16 7 8 12 14 17 6 22 12"></polyline>
  </svg>
);

const BarIcon = () => (
  <svg
    xmlns="http://www.w3.org/2000/svg"
    width="24"
    height="24"
    viewBox="0 0 24 24"
    fill="none"
    stroke="currentColor"
    strokeWidth="2"
    strokeLinecap="round"
    strokeLinejoin="round"
  >
    <line x1="12" y1="20" x2="12" y2="10"></line>
    <line x1="18" y1="20" x2="18" y2="4"></line>
    <line x1="6" y1="20" x2="6" y2="16"></line>
  </svg>
);

const PieIcon = () => (
  <svg
    xmlns="http://www.w3.org/2000/svg"
    width="24"
    height="24"
    viewBox="0 0 24 24"
    fill="none"
    stroke="currentColor"
    strokeWidth="2"
    strokeLinecap="round"
    strokeLinejoin="round"
  >
    <path d="M21.21 15.89A10 10 0 1 1 8.11 2.99"></path>
    <path d="M22 12A10 10 0 0 0 12 2v10z"></path>
  </svg>
);

const GRAPH_TYPES = [
  { id: "line", label: "Line", icon: <LineIcon /> },
  { id: "bar", label: "Bar", icon: <BarIcon /> },
  { id: "pie", label: "Pie", icon: <PieIcon /> },
];

const GraphTypeSidebar = ({ selectedGraphType, onGraphTypeChange }) => {
  return (
    <div className="sidebar">
      <h3>Graph Types</h3>
      <div className="sidebar-buttons">
        {GRAPH_TYPES.map((type) => (
          <button
            key={type.id}
            className={selectedGraphType === type.id ? "active" : ""}
            onClick={() => onGraphTypeChange(type.id)}
            title={type.label}
          >
            {type.icon}
            <span>{type.label}</span>
          </button>
        ))}
      </div>
    </div>
  );
};

export default GraphTypeSidebar;
